mapper.select:
SELECT cpm.PLAN_NO AS planNo ,cpm.PLAN_TITLE AS planTitle ,cpm.PLAN_DESC AS planDescription ,cpm.TRIP_START_DATE AS tripStartDate ,cpm.TRIP_END_DATE AS tripEndDate ,cpm.TRIP_TERM AS tripTerm ,cpm.LIKE_REG_CNT AS likeRegistrationCnt ,cpm.ACHV_RATE AS achieveRate ,cpm.VIEWS AS views ,cpm.REV_AVG_SCORE AS reviewAverageScore ,cam.SIDO_NM AS sidoName ,cam.SIGUNGU_NM AS sigunguName ,mmm.NICK_NM AS leaderNickName ,cpm.REG_DTIME AS registrationDateTime FROM CT_PLAN_MGMT cpm LEFT JOIN CT_PLAN_MATE mate ON cpm.PLAN_NO = mate.PLAN_NO LEFT JOIN CT_TRIP_ADDR cta ON cpm.PLAN_NO = cta.PLAN_NO LEFT JOIN CT_PLAN_ATTR cpa ON cpm.PLAN_NO = cpa.PLAN_NO LEFT JOIN CT_POST_MGMT post ON cpm.PLAN_NO = post.PLAN_NO JOIN MB_MBR_MGMT mmm ON mate.MBR_NO = mmm.MBR_NO JOIN CT_ADDR_MGMT cam ON cta.ADDR_NO = cam.ADDR_NO WHERE mate.LEAD_YN = 'Y' AND cpm.PUBLIC_YN = 'Y'
AND YEAR(NOW())-LEFT(mmm.BDAY, 4) + 1 >= #{age:VARCHAR}
AND YEAR(NOW())-LEFT(mmm.BDAY, 4) + 1 BETWEEN #{age:VARCHAR} AND #{age:VARCHAR} + 9
AND mmm.GEND_CD = #{genderCode:VARCHAR}
AND (SELECT COUNT(mate.MATE_NO) FROM CT_PLAN_MGMT plan, CT_PLAN_MATE mate WHERE plan.PLAN_NO = mate.PLAN_NO AND plan.PLAN_NO = cpm.PLAN_NO)
= #{personnel:VARCHAR}
BETWEEN 3 AND 5
BETWEEN 6 AND 10
BETWEEN 11 AND 20
BETWEEN 21 AND 30
> 30
AND cam.ADDR_NO IN
#{item}
AND post.SPOT_ADDR IN
#{item}
AND cpm.TRIP_TERM BETWEEN #{tripTerm:VARCHAR} AND #{tripTerm:VARCHAR} + 6
AND cpm.TRIP_TERM BETWEEN #{tripTerm:VARCHAR} - 5 AND #{tripTerm:VARCHAR} + 5
AND cpm.TRIP_TERM >= #{tripTerm:VARCHAR}
AND cpm.TRIP_TERM = #{tripTerm:VARCHAR}
AND cpa.ATTR_NO IN
#{item}
GROUP BY cpm.PLAN_NO;
